import React, { useRef, useState } from "react";
import { add, format, sub, differenceInHours } from "date-fns";
import { ko } from "date-fns/locale";
import { format as timezoneFormat } from "date-fns-tz";

export default function DateFnsExample() {
  const [day, setDay] = useState("");
  const dayRef = useRef(null);
  const handleDayChange = (event) => {
    setDay(format(new Date(event.target.value), "EEEE", { locale: ko }));
  };

  const dateFnsDate = new Date();
  const newDateFnsDate = add(dateFnsDate, { weeks: 1 });
  const cloneNewDateFnsDate = add(newDateFnsDate, { weeks: 1 });

  return (
    <div>
      <h1>date-fns</h1>
      <div>Immutable Check</div>
      <div>
        {format(dateFnsDate, "yyyy-MM-dd")}
        <br />
        {format(newDateFnsDate, "yyyy-MM-dd")}
        <br />
        {format(cloneNewDateFnsDate, "yyyy-MM-dd")}
      </div>
      <br />
      <div>Summer Time (NewYork)</div>
      <div>
        2018년 3월 10일 13시에 하루 더하기 :
        {timezoneFormat(add(new Date("2018-03-10 13:01:00"), { days: 1 }), "yyyy-MM-dd HH:mm:ssXXX", { timeZone: "America/New_York" })}
      </div>
      <div>
        2018년 3월 10일 13시에 24시간 더하기 :
        {timezoneFormat(add(new Date("2018-03-10 13:01:00"), { hours: 24 }), "yyyy-MM-dd HH:mm:ssXXX", { timeZone: "America/New_York" })}
      </div>
      <br />
      <div>leap years (Korea)</div>
      <div>2017년 1월 1일에서 1년 빼기 : {format(sub(new Date("2017-01-01"), { years: 1 }), "yyyy-MM-dd")}</div>
      <div>2017년 1월 1일에서 365일 빼기 : {format(sub(new Date("2017-01-01"), { days: 365 }), "yyyy-MM-dd")}</div>
      <br />
      <div>한국어로 표기하기</div>
      <div>07-17-2021 → 2021년 7월 17일로 표기</div>
      <div>{format(new Date("07-17-2021"), "yyyy년 MM월 dd일")}</div>
      <br />
      <div>일자별 요일 찾기</div>
      <div>
        <input type="date" ref={dayRef} onChange={handleDayChange} />
        <div>무슨 요일이었을까?</div>
        <div>{day}</div>
      </div>
      <br />
      <div>두 날짜 비교</div>
      <div>2021-07-17 03:00 와 2021-07-18 02:00 는 몇시간 차이인가?</div>
      <div>{`${differenceInHours(new Date("2021-07-17 03:00"), new Date("2021-07-18 02:00"))}시간`}</div>
    </div>
  );
}
