import React from "react";
import { List, ListHeader, ListItem, Button } from "react-onsenui";

export default function ListExample() {
  return (
    <>
      <List>
        <ListHeader>Thumbnails and titles</ListHeader>
        <ListItem>
          <div class="left">
            <img class="list-item__thumbnail" src="https://placekitten.com/g/40/40" />
          </div>
          <div class="center">
            <span class="list-item__title">Cutest kitty</span>
            <span class="list-item__subtitle">On the Internet</span>
          </div>
        </ListItem>
      </List>
      <br />
      <List
        dataSource={["Row 1", "Row 2"]}
        renderRow={(row, idx) => (
          <ListItem modifier={idx === this.state.data.length - 1 ? "longdivider" : null}>
            {row}
            <Button modifier="quiet" onClick={this.remove.bind(this, idx)}>
              Remove
            </Button>
          </ListItem>
        )}
      />
    </>
  );
}
